mapper.select:
SELECT ( SELECT count(*) FROM ORIGINAL_VIDEO_INFO oviv where oviv.type=2 and oviv.creator = #{userId,jdbcType=INTEGER}
and oviv.status = 1
) as audioCount, (SELECT count(*) FROM ORIGINAL_VIDEO_INFO oviv where oviv.type=1 and oviv.creator = #{userId,jdbcType=INTEGER}
and oviv.status = 1
) as mediaCount , (select count(*) from ORIGINAL_ALBUM_INFO where userId = #{userId,jdbcType=INTEGER} )as albumCount , ( SELECT count(*) FROM USER_FOLLOW WHERE followedId = #{userId,jdbcType=INTEGER} )as followCount, u.id,u.name,u.phone,u.mail,u.sex,u.description,u.category,oci.name as categoryName,u.headImg as headImg,u.description as description
,(select count(*) from USER_FOLLOW uf where (uf.followId = #{myId,jdbcType=INTEGER} or uf.followThirdId = #{myThirdId,jdbcType=INTEGER})and uf.followedId = #{userId,jdbcType=INTEGER}) as isFollow
,(select count(*) from USER_FOLLOW uf where uf.followId = #{myId,jdbcType=INTEGER} and uf.followedId = #{userId,jdbcType=INTEGER}) as isFollow
,(select count(*) from USER_FOLLOW uf where uf.followThirdId = #{myThirdId,jdbcType=INTEGER} and uf.followedId = #{userId,jdbcType=INTEGER}) as isFollow
,0 as isFollow
FROM TOPIC_USER_INFO u left join USER_CATEGORY oci on u.category = oci.id WHERE u.id = #{userId,jdbcType=INTEGER}

select tui.id,tui.name,tui.phone,tui.description,tui.sex,tui.account,tui.category ,tui.headImg, (select count(id) from USER_FOLLOW ufl where ufl.followedId = tui.id) followCount from TOPIC_USER_INFO tui
tui.status=1
and tui.category = #{categoryId,jdbcType=INTEGER}
and tui.id not in (select uf.followedId from USER_FOLLOW uf where uf.followId =#{userId,jdbcType=INTEGER} or uf.followThirdId = #{userThirdId,jdbcType=INTEGER}) and tui.id !=#{userId,jdbcType=INTEGER}
and tui.id not in (select uf.followedId from USER_FOLLOW uf where uf.followId =#{userId,jdbcType=INTEGER}) and tui.id !=#{userId,jdbcType=INTEGER}
and tui.id not in (select uf.followedId from USER_FOLLOW uf where uf.followThirdId =#{userThirdId,jdbcType=INTEGER})
order by id desc limit #{pageNo,jdbcType=INTEGER} , #{pageSize,jdbcType=INTEGER}

select tui.id,tui.name,tui.phone,tui.description,tui.sex,tui.account,tui.category ,tui.headImg, (select count(id) from USER_FOLLOW ufl where ufl.followedId = tui.id) followCount from TOPIC_USER_INFO tui
tui.status=1
and tui.category = #{categoryId,jdbcType=INTEGER}
and tui.id in (select uf.followedId from USER_FOLLOW uf where uf.followId =#{userId,jdbcType=INTEGER} or uf.followThirdId = #{userThirdId,jdbcType=INTEGER}) and tui.id !=#{userId,jdbcType=INTEGER}
and tui.id in (select uf.followedId from USER_FOLLOW uf where uf.followId =#{userId,jdbcType=INTEGER}) and tui.id !=#{userId,jdbcType=INTEGER}
and tui.id in (select uf.followedId from USER_FOLLOW uf where uf.followThirdId =#{userThirdId,jdbcType=INTEGER})
order by followCount desc limit #{pageNo,jdbcType=INTEGER} , #{pageSize,jdbcType=INTEGER}
